sp_widget.client_script:
function($scope, spUtil) {

	var c = this;

	_createChart();


	function _createChart() {

		c.myChart = new Highcharts.Chart({

			// This is were we set the configuration
			// parameters that are unique to each
			// type of chart
			chart: {
				type: 'bar', // What Chart Type
				renderTo: 'empCertBarDiv' // The DIV ID to render it into
			},
			title: {
				text: 'Employee Certification Completion'
			},
			exporting: {
				enabled: false
			},
			credits: {
				enabled: false
			},
			xAxis: {
				categories: ['Ethics Training', 'Workplace Behavior', 'Security Practices', 'Other Non-Mandatory'],
				title: {
					text: null
				}
			},
			yAxis: {
				min: 0,
				title: {
					text: 'Percent Complete',
					align: 'high'
				},
				labels: {
					overflow: 'justify'
				}
			},
			tooltip: {
				valueSuffix: ''
			},
			plotOptions: {
				bar: {
					dataLabels: {
						enabled: true
					}
				}
			},
			legend: {
//				layout: 'horizontal',
//				align: 'center',
//				verticalAlign: 'bottom',
//				x: -40,
//				y: 80,
//				floating: false,
//				borderWidth: 1,
//				backgroundColor: ((Highcharts.theme && Highcharts.theme.legendBackgroundColor) || '#FFFFFF'),
//				shadow: true
			},
			series: c.data.mySeriesData // Set the series data from server script

		}); // END - Configuration parameters	

	} // END - _createChart method

} // END - Client Script

sp_widget.css:
