sp_widget.client_script:
function($http) {
	/* widget controller */
	var c = this;

	// Set default justPop value to Answers
	c.justPop = 'Answers';

	// Replace hard coded DIV id with dynamic one
	document.getElementById('replaceMeInClientScript')
		.setAttribute("id", c.data.mcdid);	

	// Create the chart object without
	// data, enable the "Loading" indicator.
	c.myChart = {};
	_createChart();
	c.myChart.showLoading();



	// Make call to appropriate API to get
	// data. Update chart and disable 
	// "Loading" indicator when data is returned
	$http.get('/api/x_saic4_persona_ss/client_utils/getArticlesByCat?justPop='+c.justPop)
		.then(function(res) {
		c.data.chartData = res.data.result;
		c.myChart.series[0].setData(c.data.chartData);
		c.myChart.hideLoading();
	});	

		// Functions that emit events
	c.onFiltChange = function(justPop) {
		c.justPop = justPop;
		c.myChart.showLoading();
		$http.get('/api/x_saic4_persona_ss/client_utils/getArticlesByCat?justPop='+c.justPop)
			.then(function(res) {
			c.data.chartData = res.data.result;
			c.myChart.series[0].setData(c.data.chartData);
			c.myChart.series[0].name = justPop;
			c.myChart.hideLoading();
		});	

	};



	// Function to create and render the
	// chart object.
	function _createChart() {

		c.myChart = new Highcharts.Chart({
			chart: {
				type: 'wordcloud',
				renderTo: c.data.mcdid
			},
			title: {
				text: null,
				enabled: false
			},
			exporting: {
				enabled: false
			},
			credits: {
				enabled: false
			},
			loading: {
				hideDuration: 1000,
				showDuration: 1000
			},
			plotOptions: {
				series: {
					cursor: 'pointer',
					animation: {
						duration: 2000
					},
					point: {
						events: {
							click: function () {
								if (c.justPop == "Searches") {
									location.href = '/upssp?id=kb_search&spa=1&query='+this.options.name;
								} else {
									location.href = '/upssp?id=kb_search&kb_knowledge_base=3b65e7e1dbed7f40f42f32dc7c9619c1&spa=1&kb_category=' +
									this.options.katSys;
								}
							}
						}
					}
				}
			},
			series: [{
				name: c.justPop,
				colors: c.data.chartColors,
				data: c.data.chartData,
				rotation: {
					from: 0,
					to: 0,
					orientations: 5
				}

			}]
		});	
	}

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	// Get a unique id for the chart's
	// containing DIV.
	data.mcdid = gs.generateGUID();

	var spc = new SSPChartUtils();
	data.chartColors = spc.getPaletteByName("catLong");

	data.chartData = [];


})();
