sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.panelTitle = "Our Services";
	data.svcCategories = [
		"Finance",
		"Human Resources",
		"Technology",
		"Facilities"
	];
	data.svcModes = [
		"Help",
		"Services",
		"Answers"
	];
	data.currentCategory = -1;
	data.currentMode = -1;


})();
