	////////////////////////////////////////
	// Method returns the default list of 
	// widgets/options for users that have
	// not already stored a preference
	///////////////////////////////////////
	getDefaultWidgetList: function() {

		// Get the timezone of the current user's profile
		var timeZone = gs.getSession().getTimeZoneName();

		var widgetList = [
			{"id" : "current-status", "options" : {"title": "Current Service Status"}},
			{"id" : "announcements", "options" : {"title": "Announcements"}},
		];

		return widgetList;
	},

	///////////////////////////////////////
	// Method returns the default list of 
	// available layout templates
	///////////////////////////////////////
	getTemplateListing: function() {

		var templates = [
			{"title" : "Simple", "tpName"     : "sspSimpleTemplate"},
//			{"title" : "Alt Simple", "tpName" : "sspAltSimpleTemplate"},
			{"title" : "Moderate", "tpName"   : "sspModerateTemplate"},
			{"title" : "Power User", "tpName" : "sspPowerUserTemplate"},
			{"title" : "Max User", "tpName"   : "sspMaxUserTemplate"},
		];

		return templates;
	},

	///////////////////////////////////////////
	// Given a template's title, returns the
	// index of that template's object within
	// the provided list of templates
	getTemplateIndexFromTitle: function(title, templates) {
		for (var i = 0; i < templates.length; i++) {
			if (templates[i]["title"] === title) {
				return i;
			}
		}
		return 0;
	},

    type: 'SSPMegaUtil'
